import React, { Component } from 'react';
import '../stylesheets/App.css';
import { ProgressBar, Container, ToggleButton, Col, Row, Navbar, Nav, NavDropdown, Form, FormControl, Button, Carousel, Image, Table, Jumbotron } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';

let state = 0;

function changeState() {
    if (state === 0) {
        return (
            <div>
            <h1>Active</h1>
            <ToggleButton onChange={state = Math.abs(state - 1)} variant="success" active>Following</ToggleButton>
            </div>)
    }
    return (
        <div>
        <h1>Inactive</h1>
        <ToggleButton onChange={state = Math.abs(state - 1)} variant="outline-success" >Follow Project</ToggleButton>
        </div>
    )
        }
        
class FollowProject extends Component {
    render() {
        return (
            <div>
            <h1>hi</h1>
                <changeState />
                </div>
   );
    }
}

export default FollowProject;
