import React, { Component } from 'react';
import '../stylesheets/App.css';
import { ToggleButton } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';

let state = 0;

function updateState() {
    state = Math.abs(state - 1)
}

function FollowingButton() {
    if (state === 0) {
        return (
            <div>
            <h1>Active</h1>
            <ToggleButton type = "checkbox" onChange={updateState} variant="success" active>Following</ToggleButton>
            </div>)
    }
    return (
        <div>
        <h1>Inactive</h1>
        <ToggleButton type = "checkbox" onChange={updateState} variant="outline-success" >Follow Project</ToggleButton>
        </div>
    )
        }
        
class FollowProject extends Component {
    render() {
        return (
            <div>
            <h1>hi</h1>
                <FollowingButton />
                </div>
   );
    }
}

export default FollowProject;
